import React,{useState,useContext,useEffect} from 'react';
import styled from 'styled-components';
import {Checkbox,TextField,Tab,Tabs,Paper,InputLabel,MenuItem,Selec} from '@material-ui/core'
import {makeReportSearchRequest}  from '../../request/requset'
import {Context} from '../../Context/Context';
import DateFnsUtils from '@date-io/date-fns';
import {MuiPickersUtilsProvider,KeyboardDatePicker} from '@material-ui/pickers'

const SearchModalContainer = styled.form`
position:fixed;
display:flex;
justify-content:space-around;
align-items:center;
flex-flow:column;
width:50%;
height:70%;
margin:0 auto;
left:0;
right:0;
border-radius:6px;
padding:15px;
box-sizing:border-box;
top:12%;
z-index:7;
background:white;
box-shadow: 0 1px 6px -1px rgba(0, 0, 0,0.4), 0 2px 4px -1px rgba(0, 0, 0, 0.02);

@media (max-width: 600px) {
    width:95%;
    height:60%;
}

`
const CloseIcon = styled.div`
position:absolute;
right:13px;
top:5px;
font-size:18px;
color:#f57170;
&:hover{
    cursor:pointer;
}
`

const SearchFields = styled.div`
display:flex;
flex-wrap:wrap;
padding:5px;
width:100%;
flex:1;
align-content:space-evenly;
justify-content:space-around;
`

const SubmitButton = styled.button`
outline:none; 
background:#00bdaa;
border:none;
align-self:flex-end;
color:white;
border-radius:5px;
padding:7px 13px ;
&:hover{
    cursor:pointer;
}
`
const initialSearchState = {
 reportType:'',
 relatedPersonName:'',
 relatedPersonPhoneNumber:'',
 dateIntervalStart:'',
 dateIntervalEnd:'',
 schoolName:'',
}

const initialSearchState2 = {
 reportType:'',
 relatedPersonName:'',
 relatedPersonPhoneNumber:'',
 dateIntervalStart:'',
 dateIntervalEnd:'',
 schoolName:'',
 whoseDocument:'',
}

export const  SearchReportModal =React.memo(({isOpen,close,role,closeModalOnly,setReports ,setSubPagesCount,setNotFound, setMainSearchData , setSelectedSubPage }) => {
  
    const [searchData,setSearchData] = useState (null);
    const [date,setDate] = useState(null);
    const [date2,setDate2] = useState(null);
    const {isLoggedinf}  = useContext(Context); 
    

    useEffect(()=>{ 
        role === 'Admin' ? setSearchData(initialSearchState2) : setSearchData(initialSearchState);   
    },[role])

    useEffect(() => {
        setSearchData((prev)=>({...prev,dateIntervalStart:date}))
    }, [date])

    useEffect(() => {
        setSearchData((prev)=>({...prev,dateIntervalEnd:date2}))
    }, [date2])
  
    const searchSubmitHandler = (e)=>{
        e.preventDefault();

        closeModalOnly(false);
        setSelectedSubPage(0);
        
        const searchMainData = {...searchData};

        for (const key in searchMainData){
            if(searchMainData[key] && key !== 'dateIntervalStart' && key !== 'dateIntervalEnd')   searchMainData[key]  = searchMainData[key].trim();            
        }

        setMainSearchData(searchMainData);  //we can also send trimmed data to parent component;

        makeReportSearchRequest('post',searchMainData, isLoggedinf , setReports , close , setSubPagesCount,()=>{},setNotFound);      
        
    }

    const submitChangeHandler = Type => event => {
      let value = event.target.value; 
      setSearchData((prevState)=>({...prevState,[Type]:value})); // react uses synthetic event istead of dom event
    }      

    return isOpen ? <SearchModalContainer onSubmit={searchSubmitHandler} >

        <CloseIcon onClick={close}>
           <i class="fas fa-times"></i>
        </CloseIcon>

        <SearchFields>

            <TextField style={{width:'45%',fontSize:14}} inputProps={{style:{fontSize:14}}}  InputLabelProps={{style:{fontSize:14}}}    value={searchData['reportType']}  onChange={submitChangeHandler('reportType')}  id="select" label="GÃ¶rÃ¼ÅŸme Tipi"  select>
                        <MenuItem value="schoolReport">Okul GÃ¶rÃ¼ÅŸmesi</MenuItem>
                        <MenuItem value="studentReport">Veli/Ã–ÄŸrenci GÃ¶rÃ¼ÅŸmesi</MenuItem>
            </TextField>

            
            {
                role === 'Admin' ?  <TextField  InputLabelProps={{style:{fontSize:14}}} inputProps={{style:{fontSize:14}}}   style={{width:'45%'}} value={searchData['whoseDocument']}  onChange={submitChangeHandler('whoseDocument')}  id="standard-basic" label="GÃ¶nderen  KiÅŸi"/> : null 
            }
      
            <TextField   style={{width:'45%'}}  inputProps={{style:{fontSize:14}}}  InputLabelProps={{style:{fontSize:14}}} value={searchData['relatedPersonName']}  onChange={submitChangeHandler('relatedPersonName')}  id="standard-basic" label="GÃ¶rÃ¼ÅŸÃ¼len KiÅŸi" />

            <TextField   style={{width:'45%'}}  inputProps={{style:{fontSize:14}}} InputLabelProps={{style:{fontSize:14}}} value={searchData['relatedPersonPhoneNumber']}  onChange={submitChangeHandler('relatedPersonPhoneNumber')}  id="standard-basic" label="GÃ¶rÃ¼ÅŸÃ¼len KiÅŸinin Telefon NumarasÄ±" />

            <TextField   style={{width:'45%'}}   inputProps={{style:{fontSize:14}}} InputLabelProps={{style:{fontSize:14}}} value={searchData['schoolName']}  onChange={submitChangeHandler('schoolName')}  id="standard-basic" label="Okul Ä°smi" />

        
         <MuiPickersUtilsProvider utils={DateFnsUtils}>

         <KeyboardDatePicker disableToolbar
         inputProps={{style:{fontSize:14}}}
         InputLabelProps={{style:{fontSize:14}}}
          variant="inline"
          format="dd/MM/yyyy"
          margin="normal"
          id="date-picker-inline"
          label="BaÅŸlangÄ±Ã§ Tarih"   
          value={searchData['dateIntervalStart']} 
          onChange={setDate} 
          style={{width:'45%',margin:'10px 0'}}/>

        <KeyboardDatePicker disableToolbar
        inputProps={{style:{fontSize:14}}}
         InputLabelProps={{style:{fontSize:14}}}
          variant="inline"
          format="dd/MM/yyyy"
          margin="normal"
          id="date-picker-inline"
          label="Son Tarih"   
          value={searchData['dateIntervalEnd']} 
          onChange={setDate2} 
          style={{width:'45%',margin:'10px 0'}}/>

         </MuiPickersUtilsProvider>

        </SearchFields>

        <SubmitButton type='submit'> <i class="fas  fa-search"></i> ARA </SubmitButton>
             
  </SearchModalContainer> : null;


});
