import React from 'react';
import styled from 'styled-components';
import {Link,Redirect}  from 'react-router-dom'
import {makeAuthenticationRequest,makeVerifyRequest}   from '../../request/requset'
import App from '../../App';
import {Context} from '../../Context/Context'
import Circle from '../../UI/Circle'
import {UpdateLoggedin} from '../isLoggedin/action'

const Container = styled.div`
width:100%;
background:#f3f5f9;
display:flex;
flex-direction: column;
align-items: center;
`
const Fields  = styled.form`
margin-top:30px;
max-width:350px;
padding:120px 30px 120px 30px ;
display:flex;
flex-direction: column;
align-items: center;
justify-content: center;
background:#fff;
position:relative;
@media (max-width: 1030px) {
    margin-top:0;
}

`
const Input = styled.input`
width:100%;
border-radius: 4px;
outline: none;
border:0px;
padding:10px 10px 10px 30px;
font-size:14px;
box-shadow:0 0 5px  rgb(112, 112, 112);
margin-top:10px;
`
const Inner_Fields = styled.div`
width:100%;
margin-top:10px;
padding: 5px;
display:flex;
flex-direction: column;
align-items: center;
justify-content: center;
` 


const Submit = styled.button`
align-self:flex-end;
background-color:#00bdaa;
color:white;
width:100%;
border-radius:3px;
text-align:center;
box-shadow:0 0 5px  rgb(112, 112, 112);
border:none;
margin-top:10px;
padding:5px;
display:flex;
align-items: center;
justify-content: center;
&:hover{
    cursor:pointer;
}
`
const InputWrapper = styled.div`
width:100%;
position:relative
`
const ImageWrapper = styled.div`
width:100%;
`

const SubButtons = styled.button`
padding:0;
width:98%;
out-line:none;
box-shadow:0 0 5px  rgb(112, 112, 112);
border:none;
color:white;
border-radius:3px;
margin-top:10px;
background-color:#1eb2a6;
font-weight:400;
&:hover{
    cursor:pointer;
}
`
const PopUp =styled.div`
background-color:#F3565D;
display:flex;
align-items: center;
justify-content:center;
width:100%;
padding:5px; 
color:white;
border-radius: 4px;
font-size:13px;
position:relative;

`


class Login  extends React.Component {

    state={
        user:{ 
        name:'',
        password:'',    
        },
        warning:false,
        redirect:false,
    } 
    
    redirectTo=(type)=>{
        this.setState({...type})
    }
    
    static contextType = Context;

    submitHandler=(e)=>{
            e.preventDefault();      

            const user = {name: this.state.user.name.trim(), password:this.state.user.password.trim()}; // we can prevenet fails by putting trim here
            
           if(this.state.user.name && this.state.user.password)
           {
                makeAuthenticationRequest('post',user,this.redirectTo,this.context);
           }
           else
           {
               return this.setState({warning:true})
           }
    }
    

    changeHandlerFactory = (type)=>{
        return (e)=>{
            this.setState({user:{...this.state.user,[type]:e.target.value},warning:false})
        }
    }

    render() {
        
  
        return (  
        <UpdateLoggedin page='LOGÄ°N' isLoggedin={this.state.isLoggedin} {...this.props}>
            {
                ({Loading})=>(
                    <Container>     
                    <Fields onSubmit={this.submitHandler}>
                     
                        <Circle Load={Loading} top={10} left={42}/>
                      
                        <ImageWrapper>
                            <img style={{width:'100%'}} alt="ercan" src='cropped-kanada-lisediplomasÄ±.png' ></img>
                        </ImageWrapper>
                        
                        <Inner_Fields>
                            {this.state.warning ? <PopUp>HatalÄ± GiriÅŸ !<i onClick={()=>this.setState({warning:false})}  style={{position:'absolute',right:5,color:'#a94442' , top:5}}  className="fas fa-times"></i></PopUp> : null}
                            <InputWrapper>
                               <i style={{display:'block',position:'absolute',left:8,top:'50%',color:'#ccc'}}  className="fas fa-user"/>
                               <Input onChange={this.changeHandlerFactory('name')}   placeholder='KullanÄ±cÄ± AdÄ±'/>
                            </InputWrapper> 
   
                             <InputWrapper>  
                               <i style={{display:'block',position:'absolute',left:8,top:'50%',color:'#ccc'}}  className="fas fa-lock"/>
                               <Input type='password' onChange={this.changeHandlerFactory('password')}  placeholder='Åžifre'/>
                            </InputWrapper>
   
                               <Submit type='Submit'>GiriÅŸ<i style={{marginLeft:5 }} className="fas fa-sign-in-alt"></i></Submit>
   
                         </Inner_Fields>
   
                         <SubButtons><Link style={{width:'100%',padding:5,display:'block',height:'100%',textDecoration:'none',color:'white'}} className='loginLink'  to='/register'><i style={{marginRight:10}} className="fas fa-file-alt"></i>Ã–n KayÄ±t BaÅŸvurusu</Link></SubButtons>
                    </Fields>
   
               </Container>
                )
            }  
        </UpdateLoggedin>    
        );
    }
}

 
export default Login ;
